DROP TABLE IF EXISTS `hubungi_kami`;

CREATE TABLE `hubungi_kami` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nama_pengirim` varchar(200) DEFAULT NULL,
  `email_pengirim` varchar(200) DEFAULT NULL,
